function e(n,r=2){return n==null||Number.isNaN(n)?"-":Number(n).toLocaleString("zh-CN",{maximumFractionDigits:r,minimumFractionDigits:r>0?Math.min(r,2):0})}function i(n){return n==null||Number.isNaN(n)?"-":`${n>0?"+":""}${Number(n).toFixed(2)}%`}function u(n){if(!n)return"-";const r=new Date(n);return Number.isNaN(r.getTime())?n:r.toLocaleString("zh-CN",{hour12:!1})}function o(n){if(!n)return null;const r=new Date(n);return Number.isNaN(r.getTime())?null:Math.max(0,Math.floor((Date.now()-r.getTime())/1e3))}function f(n){return n==null?"":n>0?"up":n<0?"down":""}function a(n){if(n==null||Number.isNaN(n))return"-";const r=Number(n)*100;return`${r>0?"+":""}${r.toFixed(4)}%`}function s(n){if(n==null)return"";const r=Number(n)*100;return r>.05?"up":r<-.05?"down":""}function c(n){return n==null?"ratio-neutral":n>=1?"ratio-good":n>=.7?"ratio-mid":"ratio-bad"}function m(n){return String(n??"").replaceAll("&","&amp;").replaceAll("<","&lt;").replaceAll(">","&gt;").replaceAll('"',"&quot;").replaceAll("'","&#39;")}export{e as a,i as b,s as c,a as d,m as e,u as f,f as g,c as h,o as s};
